.bounce_ball-enter,
.bounce_ball-leave-to {
  transform: translateY(-100%);
  opacity: 0;
}
.index_wrap {
  width: 100%;
  position: relative;
  box-sizing: border-box;
}
.index_wrap .card_wrap {
  padding: 20px;
  padding-top: 39px;
}
.index_wrap .card_wrap .card_item {
  margin: 10px 0;
  overflow: hidden;
  background: #fff;
  transition: all 300ms linear;
}
.index_wrap .card_wrap .y_active:active {
  background: #acffa1;
}
.index_wrap .card_wrap .n_active:active {
  background: #d3d3d3;
}
.desc_txt {
  width: 100%;
  height: 39px;
  font-size: 15px;
  background: #FAF7C7;
  padding: 12px 20px;
  box-sizing: border-box;
  position: absolute;
  top: 0;
  left: 0;
  transition: all 500ms linear;
}
.desc_img {
  width: 20px;
  height: 20px;
  margin-left: 5px;
  display: inline-block;
  vertical-align: bottom;
}
.desc_model {
  width: 100%;
  position: absolute;
  height: 100%;
  z-index: 22;
  left: 0;
  top: 0;
  background: #FAF7C7;
}
.desc_model .content {
  padding: 15px;
}
.desc_model .content h4 {
  font-size: 16px;
  font-weight: lighter;
  color: #f16c43;
}
.desc_model .content p {
  font-size: 14px;
  color: #4A4A4A;
}
.desc_model .content .item {
  margin: 10px 0;
}
